{
  "description": "A subscription to get info about myEntity",
  "subject": {
    "entities": [
      {
        "id": "myEntity",
        "type": "myType"
      }
    ],
    "condition": {
      "attrs": [ ]
    }
  },
  "notification": {
    "http": {
      "url": "http://putsreq.com/aGSZ2hnoTXRaO4JHZNY1"
    },
    "attrs": [
      "temperature"
    ]
  }
}

{
  "description": "A subscription to get info about myEntity",
  "subject": {
    "entities": [
      {
        "id": "myEntity",
        "type": "myType"
      }
    ],
    "condition": {
      "attrs": [ ]
    }
  },
  "notification": {
    "httpCustom": {
      "url": "http://putsreq.com/aGSZ2hnoTXRaO4JHZNY1",
      "headers": {
      	"Content-Type": "application/xml",
      	"X-EntityType": "${type}"
      },
      "qs": {
      	"entityId": "${id}"
      },
      "payload": "%3Ctemperature%3E${temperature}%3C/temperature%3E"
    },
    "attrs": [
      "temperature"
    ]
  }
}

{
    "name": "LowTemperature",
    "text": "select *,\"LowTemperature\" as ruleName from pattern [every ev=iotEvent((cast(`id`?, String)  regexp  \"myEntity\") AND (cast(`type`?, String)  =  \"myType\") AND (cast(cast(`temperature`?, String), float)  <  10))]",
	"action": {
        "type": "email",
        "template": "Entity ${id} temperature is ${temperature}",
        "parameters": {
            "to": "john_doe@fakedomain.com",
            "from": "no-reply@iotplatform.telefonica.com",
            "subject": "Low temperature"
        }
    }
}

{
    "name": "BatteryLow",
    "text": "select *,\"BatteryLow\" as ruleName from pattern [every ev=iotEvent((cast(`type`?, String)  =  \"Lamp\") AND (cast(cast(`battery`?, String), float)  <  10))]",
	"action": {
        "type": "update",
        "parameters": {
            "id": "TestEntity1",
            "type": "TestEntity",
            "attributes": [
            	{
            		"name": "msg",
            		"value": "Battery in ${id} is ${battery}"
            	}
            ]
        }
    }
}

{
	"name":"streetlight_NGSIv2Values",
	"text":"select (cast(cast(ev.illuminanceLevel?, string),float) * 100) as ilumLevel, ev.id? as id, ev.status? as laststatus, ev.powerState? as powerState, ev.areaServed? as areaServed, ev.dateLastLampChange__iso? as lastchange, ev.address__streetAddress? as streetAddress, ev.address__addressLocality? as addressLocality, ev.address__postalCode? as postalCode, ev.location__type? as locationType, ev.location__lat? as locationLat, ev.location__lon? as locationLon, \"streetlight_NGSIv2Values\" as ruleName from pattern [every ev=iotEvent(type='Streetlight')]",
	"action": [{
		"type":"update",
		"parameters":{
			"id":"${id}_NGSIv2Test",
			"type":"NGSIv2TypesTest",
			"version": "2",
			"attributes": [
				{
					"name":"streetLightID",
					"type": "Text",
					"value": "${id}"
				},
				{
					"name":"illuminanceLevel",
					"type": "Number",
					"value": "${ilumLevel}"
				},
				{
					"name":"lastchange",
					"type": "DateTime",
					"value": "${lastchange}"
				},
				{
					"name":"district",
					"type": "Text",
					"value": "${areaServed}"
				},
				{
					"name":"status",
					"type": "Text",
					"value": "${laststatus}"
				},
				{
					"name":"address",
					"type": "Address",
					"value": "${streetAddress} ${postalCode}, ${addressLocality}"
				},
				{
					"name":"powerState",
					"type": "Text",
					"value": "${powerState}"
				},
				{
					"name":"location",
					"type": "${locationType}",
					"value": "${locationLat}, ${locationLon}"
				}
			]
		}
	}]
}

