{
    "name": "streetlight_column_alarm_with_mail",
    "text": "select ev.illuminanceLevel? as ilumLevel, ev.id? as id, ev.status? as laststatus, ev.powerState? as powerState, ev.areaServed? as areaServed, ev.dateLastLampChange__iso? as lastchange, ev.address__streetAddress? as streetAddress, ev.address__addressLocality? as addressLocality, ev.address__addressCountry? as addressCountry, ev.address__postalCode? as postalCode, \"streetlight_column_alarm_with_mail\" as ruleName from pattern [every ev=iotEvent(type='Streetlight' and ev.status? = 'columnIssue')]",
    "action": [
        {
            "action": {
                "type": "post",
                "template": "{\"type\":\"command\",\"value\":1}",
                "parameters": {
                    "url": "http://10.0.0.2:1026/v2/entities/${electrovalveId}/attrs/activate?type=electrovalve",
                    "method": "PUT",
                    "headers": {
                        "Content-type": "application/json",
                        "fiware-service": "${service}",
                        "fiware-servicepath": "${subservice}"
                    }
                }
            }
        }
    ]
}

{
	"name":"Area_Alert",
	"text":"select *, \"Area_Alert\" as ruleName FROM iotEvent(type='Streetlight').win:time(1 minute) MATCH_RECOGNIZE(measures A.areaServed? as areaServed, C.dateLastLampChange__iso? as lastchange, A.id? as idA, B.id? as idB, C.id? as idC pattern (A B C) define A as A.status? = 'defectiveLamp', B as B.status? = 'defectiveLamp', C as C.status? = 'defectiveLamp' and B.id? != A.id? and B.id? != C.id? and A.id? != C.id? and B.areaServed? = A.areaServed? and B.areaServed? = C.areaServed?)",
	"action": [{
		"type":"update",
		"parameters":{
			"id":"area_alert_${areaServed}",
			"type":"AreaAlert",
			"version": "2",
			"attributes": [
				{
					"name":"area",
					"type": "Text",
					"value": "${areaServed}"
				},
				{
					"name":"lastchange",
					"type": "DateTime",
					"value": "${lastchange}"
				},
				{
					"name":"streetLights",
					"type": "StructuredValue",
					"value": "[${idA}, ${idB}, ${idC}]"
				}
			]
		}
	}]
}

{
	"name":"Timer_example",
	"text":"select *, current_timestamp.toDate() as currentTS, \"Timer_example\" as ruleName from pattern [every timer:at(20, *, *, *, *)]",
	"action": [{
		"type":"update",
		"parameters":{
			"id":"timer_example_min_2",
			"attributes": [
				{
					"name":"timestamp",
					"type": "Text",
					"value": "${currentTS}"
				}
			]
		}
	}]
}

{
   "name":"simple_timed_rule",
   "text":"select *, current_timestamp().toDate() as currentTS, \"simple_timed_rule\" as ruleName from pattern [every timer:interval(60 sec)]",
   "action":{
		"type":"update",
        "parameters":{
            "id":"temp_example_10_sec",
            "type":"temp_example",
            "version": "2",
            "attributes": [
                {
                    "name":"Text",
                    "value":"${currentTS}"
                }
           ]
        }
	}
}
