{
    "name": "alarm_luminosity",
    "description": "Crea una alarma si la luminosidad baja de 20",
    "text": "select \"alarm_luminosity\" as ruleName, *, municipality?, luminosity?, location__lat? as lati, location__lon? as longi, current_timestamp().format(\"yyyy-MM-dd'T'HH:mm:ss.SSSX\") as timeinstant from iotEvent where type='Streetlight' and not exists(error_alarma_lampara?) and cast(powerState?, String) = \"on\" and cast(cast(luminosity?, String), float) <= 20",
    "action": [
        {
            "type": "update",
            "parameters": {
                "id": "malfunction:${id}",
                "type": "Alert",
                "version": 2,
                "subservice": "/alarmas",
                "attributes": [
                    {
                        "name": "description",
                        "type": "Text",
                        "value": "Lampara fundida. Medida: ${luminosity}"
                    },
                    {
                        "name": "location",
                        "type": "geo:json",
                        "value": "{\"type\": \"Point\",\"coordinates\": [${longi},${lati}]}"
                    },
                    {
                        "name": "sourceType",
                        "type": "Text",
                        "value": "${type}"
                    },
                    {
                        "name": "severity",
                        "type": "Text",
                        "value": "Medium"
                    },
                    {
                        "name": "category",
                        "type": "Text",
                        "value": "malfunction"
                    },
                    {
                        "name": "source",
                        "type": "Text",
                        "value": "${id}"
                    },
                    {
                        "name": "alertSource",
                        "type": "Text",
                        "value": "${subservice}"
                    },
                    {
                        "name": "dateIssued",
                        "type": "DateTime",
                        "value": "${timeinstant}"
                    },
                    {
                        "name": "TimeInstant",
                        "type": "DateTime",
                        "value": "${timeinstant}"
                    },
                    {
                        "name": "status",
                        "type": "Text",
                        "value": "active"
                    },
                    {
                        "name": "municipality",
                        "type": "Text",
                        "value": "${municipality}"
                    }
                ],
                "actionType": "append"
            }
        },
        {
            "type": "update",
            "parameters": {
                "id": "${id}",
                "type": "Streetlight",
                "version": 2,
                "attributes": [
                    {
                        "name": "Error-alarma_lampara",
                        "type": "Text",
                        "value": "true"
                    },
                    {
                        "name": "error_alarma_lampara",
                        "type": "Text",
                        "value": "true"
                    }
                ],
                "actionType": "append"
            }
        }
    ]
}

