    "text": "expression DOUBLE {(val)=> case when val is null or cast(cast(val,string),double) < 0 or cast(val,string) = '' then 0 else cast(cast(val,string),double) end } expression TS alias for {cast(cast(TimeInstant?, String), long,dateformat:'iso')} expression RD alias for {DOUBLE(ratioDay?)} expression RN alias for {DOUBLE(ratioNigth?)} expression RT alias for {DOUBLE(ratioTwiglight?)} expression LAT alias for {cast(cast(location__lat?,string),double)} expression LON alias for {cast(cast(location__lon?,string),double)} expression MILISTOORTO alias for {getNextSunrise(java.util.Calendar.getInstance(),39, 2).toMillisec()} expression MILISTOOCASO alias for {getNextSunset(java.util.Calendar.getInstance(),39, 2).toMillisec()} expression TWTHRESOLD alias for {case when threshold? is null then 60*60*1000 else DOUBLE(threshold?) end } expression ONEDAY alias for {24*60*60*1000} expression ISNIGHT { (ts)=> MILISTOOCASO>MILISTOORTO and (ts-(MILISTOOCASO-ONEDAY))>TWTHRESOLD and (MILISTOORTO-ts)>TWTHRESOLD } expression ISDAY { (ts)=> MILISTOORTO>MILISTOOCASO and (ts-(MILISTOORTO-ONEDAY))>TWTHRESOLD and (MILISTOOCASO-ts)>TWTHRESOLD } expression CALCULATED {(n,rd,rn,rt,ts)=> case when ISDAY(ts) then n*(1+rd) else case when ISNIGHT(ts) then n*(1+rn) else n*(1+rt) end end } select *, CALCULATED(DOUBLE(carNumberAscending?), RD,RN,RT, TS) as ascendingCar, CALCULATED(DOUBLE(carNumberDescending?), RD,RN,RT, TS) as descendingCar, CALCULATED(DOUBLE(carNumberAscending?)+DOUBLE(carNumberDescending?), RD,RN,RT, TS) as totalCar, CALCULATED(DOUBLE(lorryNumberAscending?), RD,RN,RT, TS) as ascendingLorry, CALCULATED(DOUBLE(lorryNumberDescending?), RD,RN,RT, TS) as descendingLorry, CALCULATED(DOUBLE(lorryNumberAscending?)+DOUBLE(lorryNumberDescending?), RD,RN,RT, TS) as totalLorry, CALCULATED(DOUBLE(bicycleNumberAscending?), RD,RN,RT, TS) as ascendingBicycle, CALCULATED(DOUBLE(bicycleNumberDescending?), RD,RN,RT, TS) as descendingBicycle, CALCULATED(DOUBLE(bicycleNumberAscending?)+DOUBLE(bicycleNumberDescending?), RD,RN,RT, TS) as totalBicycle, CALCULATED(DOUBLE(motorbikeNumberAscending?), RD,RN,RT, TS) as ascendingMotorbike, CALCULATED(DOUBLE(motorbikeNumberDescending?), RD,RN,RT, TS) as descendingMotorbike, CALCULATED(DOUBLE(motorbikeNumberAscending?)+DOUBLE(motorbikeNumberDescending?), RD,RN,RT, TS) as totalMotorbike, CALCULATED(DOUBLE(carNumberAscending?)+DOUBLE(carNumberDescending?)+DOUBLE(lorryNumberAscending?)+DOUBLE(lorryNumberDescending?)+DOUBLE(bicycleNumberAscending?)+DOUBLE(bicycleNumberDescending?)+DOUBLE(motorbikeNumberAscending?)+DOUBLE(motorbikeNumberDescending?), RD,RN,RT, TS) as totalVehicles, current_timestamp().format(\"yyyy-MM-dd'T'HH:mm:ss.SSSX\") as timeinstant, ISDAY(TS) as dia, ISNIGHT(TS) as noche, MILISTOORTO as orto, MILISTOOCASO as ocaso, TS as ts, LAT as lat, LON as lon from iotEvent where type = 'VehiclesCounter'",
