{
	"name":"streetlight_NGSIv2Values",
	"text":"select (cast(cast(ev.illuminanceLevel?, string),float) * 100) as ilumLevel, ev.location__coordinates__1? as locationLat, ev.location__coordinates__0? as locationLon, current_timestamp().format(\"yyyy-MM-dd'T'HH:mm:ss.SSSX\") as timeinstant, ev.id? as id, ev.status? as laststatus, ev.powerState? as powerState, ev.areaServed? as areaServed, ev.dateLastLampChange? as lastchange, ev.address__streetAddress? as streetAddress, ev.address__addressLocality? as addressLocality, ev.address__postalCode? as postalCode, \"streetlight_NGSIv2Values\" as ruleName from pattern [every ev=iotEvent(type='Streetlight')]",
	"action": [{
		"type":"update",
		"parameters":{
			"id":"${id}_NGSIv2Test",
			"type":"NGSIv2TypesTest",
			"version": "2",
			"attributes": [
				{
					"name":"streetLightID",
					"type": "Text",
					"value": "${id}"
				},
				{
					"name":"illuminanceLevel",
					"type": "Number",
					"value": "${ilumLevel}"
				},
				{
					"name":"lastchange",
					"type": "DateTime",
					"value": "${timeinstant}"
				},
				{
					"name":"district",
					"type": "Text",
					"value": "${areaServed}"
				},
				{
					"name":"status",
					"type": "Text",
					"value": "${laststatus}"
				},
				{
					"name":"address",
					"type": "Address",
					"value": "${streetAddress} ${postalCode}, ${addressLocality}"
				},
				{
					"name":"powerState",
					"type": "Text",
					"value": "${powerState}"
				},
				{
					"name":"location",
					"type": "loc",
					"value": "${locationLat}, ${locationLon}"
				}
			]
		}
	}]
}

