{
	"name":"Area_Alert",
	"text":"select *, \"Area_Alert\" as ruleName FROM iotEvent(type='Streetlight').win:time(1 minute) MATCH_RECOGNIZE(measures A.areaServed? as areaServed, C.dateLastLampChange__iso? as lastchange, A.id? as idA, B.id? as idB, C.id? as idC pattern (A B C) define A as A.status? = 'defectiveLamp', B as B.status? = 'defectiveLamp', C as C.status? = 'defectiveLamp' and B.id? != A.id? and B.id? != C.id? and A.id? != C.id? and B.areaServed? = A.areaServed? and B.areaServed? = C.areaServed?)",
	"action": [{
		"type":"update",
		"parameters":{
			"id":"area_alert_${areaServed}",
			"type":"AreaAlert",
			"version": "2",
			"attributes": [
				{
					"name":"area",
					"type": "Text",
					"value": "${areaServed}"
				},
				{
					"name":"lastchange",
					"type": "DateTime",
					"value": "${lastchange}"
				},
				{
					"name":"streetLights",
					"type": "StructuredValue",
					"value": "[${idA}, ${idB}, ${idC}]"
				}
			]
		}
	}]
}
